BEGIN;
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
DELETE FROM sqlite_sequence;
CREATE TABLE "users" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "login" varchar(255), "email" varchar(255), "crypted_password" varchar(255), "password_salt" varchar(255), "persistence_token" varchar(255) NOT NULL, "single_access_token" varchar(255), "perishable_token" varchar(255) NOT NULL, "login_count" integer DEFAULT 0 NOT NULL, "failed_login_count" integer DEFAULT 0 NOT NULL, "last_request_at" datetime, "current_login_at" datetime, "last_login_at" datetime, "current_login_ip" varchar(255), "last_login_ip" varchar(255), "created_at" datetime, "updated_at" datetime, "oauth_token" varchar(255), "oauth_secret" varchar(255), "twitter_name" varchar(255), "signup_source" varchar(255), "facebook_name" varchar(255), "facebook_uid" integer(8));
CREATE TABLE "messages" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "user_id" integer, "recipient_id" integer, "parent_id" integer, "body" text, "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_users_on_oauth_token" ON "users" ("oauth_token");
COMMIT;
